import React, { useEffect, useState } from "react";

function Chat({socket, username, room}){

    const [currMsg, setCurrMsg] = useState("");
    const [msgList, setMsgList] = useState([]);

    const sendMsg = async () => {
        if(currMsg !== ""){
            const msgData = {
                room: room,
                username: username,
                message: currMsg,
                time: new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes(),
            };

            await socket.emit("send_msg", msgData);
        }
    }

    useEffect(() => {
        const handleReceiveMsg = (data) => {
            console.log(data);
            setMsgList((list) => [...list, data]);
        };
    
        socket.on("recieve_msg", handleReceiveMsg);
    
        return () => {
            socket.off("recieve_msg", handleReceiveMsg); // Cleanup on unmount or re-run
        };
    }, []);

    return (
        <div className="chat-window">
            <div className="chat-header">
                <p>Live Chat</p>
            </div>
            <div className="chat-body">
                {msgList.map((msgContent) => {
                   return <h1>{msgContent.message}</h1>
                })}
            </div>
            <div className="chat-footer">
                <input type="text" placeholder="Hey..." onChange={(event) => {setCurrMsg(event.target.value)}} value = "text"/>
                <button onClick={sendMsg}>&#9658;</button>
            </div>
        </div>
    )
}

export default Chat;
