import React, { useEffect, useState } from "react";
import ScrollToBottom from "react-scroll-to-bottom";

function Chat({ socket, username, room }) {
  const [currMsg, setCurrMsg] = useState("");
  const [msgList, setMsgList] = useState([]);

  const sendMsg = async () => {
    if (currMsg !== "") {
      const msgData = {
        room: room,
        username: username,
        message: currMsg,
        time:
          new Date(Date.now()).getHours() +
          ":" +
          new Date(Date.now()).getMinutes(),
      };

      await socket.emit("send_msg", msgData);
      setMsgList((list) => [...list, msgData]);
      setCurrMsg("");
    }
  };

  useEffect(() => {
    const handleReceiveMsg = (data) => {
      console.log(data);
      setMsgList((list) => [...list, data]);
    };

    socket.on("recieve_msg", handleReceiveMsg);

    return () => {
      socket.off("recieve_msg", handleReceiveMsg);
    };
  }, [socket]);

  return (
    <div className="Chat">
      <div className="room-data">
        <h1 id="username">{username}</h1>
        <h1 id="room">{room}</h1>
      </div>

      <div className="chat-window">
        <div className="chat-header">
          <p>Live Chat</p>
        </div>

        <div className="chat-body">
          <ScrollToBottom className="message-container">
            {msgList.map((msgContent, index) => {
              // If it's your message, use className="message you"
              // Otherwise, className="message other"
              const isYou = msgContent.username === username;
              return (
                <div
                  key={index}
                  className={`message ${isYou ? "you" : "other"}`}
                >
                  <div className="message-content">
                    <p>{msgContent.message}</p>
                  </div>
                  <div className="message-meta">
                    <p id="time">{msgContent.time}</p>
                    <p id="author">{msgContent.username}</p>
                  </div>
                </div>
              );
            })}
          </ScrollToBottom>
        </div>

        <div className="chat-footer">
          <input
            type="text"
            placeholder="Hey..."
            value={currMsg}
            onChange={(event) => setCurrMsg(event.target.value)}
            onKeyDown={(event) => {
              if (event.key === "Enter") sendMsg();
            }}
          />
          <button onClick={sendMsg}>&#9658;</button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
